import React, { useState, useEffect } from 'react';
import axios from 'axios'
import { StyleSheet, Text, View, TouchableOpacity, ImageBackground, Dimensions,} from 'react-native'
import { IP } from '../../../env';
import { FlatList } from 'react-native-gesture-handler';
import { SearchBar } from 'react-native-elements';
import Ionicons from 'react-native-vector-icons/Ionicons';



export default  function  Products ({route, navigation}) {
    let category = route.params.params
    let iconName = "shopping-bag"
    const [cate, setCate]= useState([])
    const [name, setName]= useState("")
    const [product,setProduct] = useState([])

    useEffect(()=>{
        axios.get(`${IP}/productsCat?id=${category.id}`)
            .then(function(response){
                setCate(response.data)
            })
            .catch(error=>{
              console.log(error)  
            })
    

    },[])

    const handleSearch=(name)=>{
        setName(name)
       
            axios.get(`${IP}/products?name=${name}`)
            .then(function(response){
                setProduct(response.data)
            })
            .catch(error=>{
              console.log(error)  
            })
        
 }

    
  
    return (
        <View style={styles.container}>
            <View>
                <View
                    style={{alignItems:"center",height:50, width:400, backgroundColor:"white"}}>
                        <Text style={styles.search}>
                            Search
                        </Text>
                </View>
                <SearchBar 
                    inputContainerStyle={{backgroundColor:"white"}}
                    inputStyle={{backgroundColor: 'white'}}
                    containerStyle={{backgroundColor: 'white', borderWidth: 1, borderRadius: 5}}
                    onChangeText={handleSearch}
                    value={name}/>
            </View>
                <Text style={styles.categories}>Products</Text>
            {  
                !name ?

              <View style={styles.contProduct}>
                <FlatList
                keyExtractor={item => item.id.toString()}
                numColumns={2}
                data={cate}
                renderItem={({item})=>{
                    return(
                        
                         
                        <TouchableOpacity
                        key={item.id} onPress={() => navigation.navigate("ProductDetail",{id:item.id})}>
                        <View style={styles.product}>
                        <ImageBackground
                        key={item.id}
                        source={{ uri:item.img}}
                        imageStyle={{elevation:60}}
                        style={styles.image}     
                        >
                            
                          <Text style={ styles.nombre}>{item.name}</Text>
                       
                          </ImageBackground>
                          </View> 
                      </TouchableOpacity>
                  
                    )
                }}
                /> 
            </View> 
            :
            <FlatList
            keyExtractor={item => item.id.toString()}
            numColumns={2}
            data={product}
            renderItem={({item})=>{
                return(
                    <TouchableOpacity
                    key={item.id} onPress={() => navigation.navigate("ProductDetail",{id:item.id})}>
                    <View >
                    <ImageBackground
                    key={item.id}
                    source={{ uri:item.img}}
                    style={{ width:206, height:200}}      
                    >
                        
                      <Text  style={ styles.nombre}>{item.name}</Text>
                   
                      </ImageBackground>
                        </View>
                  </TouchableOpacity>
                )
            }}
            />
            
            
           

            }

            
        </View>
    )
        }
          
       
      
//      return  (
//         <View>
//             <Button
//         title="product detail"
//         onPress={() => navigation.navigate("ProductDetail")}
//       />
            
//         </View>
//     )
// }

const styles = StyleSheet.create({
    container:{
        alignItems:"center",
        flex:1,
        backgroundColor:"#fff",
    },
    search:{
        marginTop:5,
        fontSize: 25,
        fontWeight: "bold",
        
    },
    categories:{
        marginTop:15,
        marginBottom:15,
        fontSize:25,
        fontWeight: "bold"
    },
    nombre:{
        fontStyle: "normal",
        fontWeight: 500,
        fontSize: 20,
        marginTop:15,
        marginBottom:15,
        alignSelf:"center"
    },
    image:{
        marginHorizontal:5,
        marginVertical:5, 
        borderRadius:15,
        overflow: 'hidden', 
        height:200,
        width:Dimensions.get('window').width /2,
    },
    product:{
       elevation:100
    }
   
})
