import React, { useState, useEffect } from 'react';
import axios from 'axios'
import { StyleSheet, Text, View, TouchableOpacity, ImageBackground} from 'react-native'
import { IP } from '../../../env';
import { FlatList } from 'react-native-gesture-handler';
import { SearchBar } from 'react-native-elements';
import Ionicons from 'react-native-vector-icons/Ionicons';



export default  function  Products ({route, navigation}) {
    let category = route.params.params
    let iconName = "shopping-bag"
    const [cate, setCate]= useState([])
    const [name, setName]= useState("")
    const [product,setProduct] = useState([])

    useEffect(()=>{
        axios.get(`${IP}/productsCat?id=${category.id}`)
            .then(function(response){
                setCate(response.data)
            })
            .catch(error=>{
              console.log(error)  
            })
    

    },[])

    const handleSearch=(name)=>{
        setName(name)
       
            axios.get(`${IP}/products?name=${name}`)
            .then(function(response){
                setProduct(response.data)
            })
            .catch(error=>{
              console.log(error)  
            })
        
 }

    
  
    return (
        <View style={styles.container}>
            <Text style={styles.text}>Search</Text>
            <SearchBar 
                inputContainerStyle={{backgroundColor:"white"}}
                inputStyle={{backgroundColor: 'white'}}
                containerStyle={{backgroundColor: 'white', borderWidth: 1, borderRadius: 5}}
                onChangeText={handleSearch}
                value={name}/>
                <Text style={styles.text}>Products</Text>
                <Ionicons name={iconName}  />
            {  
                !name ?

              <View style={styles.contProduct}>
                <FlatList
                keyExtractor={item => item.id.toString()}
                numColumns={2}
                data={cate}
                renderItem={({item})=>{
                    return(
                        
                          
                        <TouchableOpacity
                        key={item.id} onPress={() => navigation.navigate("ProductDetail",{id:item.id})}>
                  
                        <ImageBackground
                        key={item.id}
                        source={{ uri:item.img}}
                        style={{ borderColor:"#E0E0E0",borderRadius:2, width:206, height:200}}     
                        >
                            
                          <Text style={ styles.nombre}>{item.name}</Text>
                       
                          </ImageBackground>
                           
                      </TouchableOpacity>
                  
                    )
                }}
                /> 
            </View> 
            :
            <FlatList
            keyExtractor={item => item.id.toString()}
            numColumns={2}
            data={product}
            renderItem={({item})=>{
                return(
                    <TouchableOpacity
                    key={item.id} onPress={() => navigation.navigate("ProductDetail",{id:item.id})}>
                    <View>
                    <ImageBackground
                    key={item.id}
                    source={{ uri:item.img}}
                    style={{ width:206, height:200}}      
                    >
                        
                      <Text  style={ styles.nombre}>{item.name}</Text>
                   
                      </ImageBackground>
                        </View>
                  </TouchableOpacity>
                )
            }}
            />
            
            
           

            }

            
        </View>
    )
        }
          
       
      
//      return  (
//         <View>
//             <Button
//         title="product detail"
//         onPress={() => navigation.navigate("ProductDetail")}
//       />
            
//         </View>
//     )
// }

const styles = StyleSheet.create({
    container:{
        display:"flex",
        flex:1,
    },
    text:{
        fontStyle: "normal",
        fontWeight: "bold",
        fontSize: 25,
        marginTop:15,
        marginBottom:15,
        alignSelf:"center"
    },
    nombre:{
        fontStyle: "normal",
        fontWeight: 500,
        fontSize: 20,
        marginTop:15,
        marginBottom:15,
        alignSelf:"center"
    },
   
   
})
